	parameter S0=0,S1=1,S2=2;

	reg[1:0]next_state;

	always@(*)begin
		case(next_state)
			S0:next_state=x?S1:S0;
			S1:next_state=x?S2:S1;
			S2:next_state=x?S1:S2;
			default:next_state=S0;
		endcase
	end

	always@(posedgeclk)begin
		if(!aresetn)begin
			next_state<=S0;
			z<=0;
		endelsebegin
			next_state<=next_state;
			z<=next_state==S2?1:0;
		end
	end

endmodule

