	function walk;
		input[1:0]state;

		case(state)
			WALK_LEFT:if(bump_left)
						walk(WALK_RIGHT);
					else
						walk(WALK_LEFT);
			WALK_RIGHT:if(bump_right)
						walk(WALK_LEFT);
					else
						walk(WALK_RIGHT);
		endcase
	endfunction

	function next;
		input[1:0]state;

		case(state)
			WALK_LEFT:if(bump_left)
						next(WALK_RIGHT);
					else
						next(WALK_LEFT);
			WALK_RIGHT:if(bump_right)
						next(WALK_LEFT);
					else
						next(WALK_RIGHT);
		endcase
	endfunction

endmodule

