	parameter WALK_LEFT = 0,
					WALK_RIGHT = 1;
	parameter FALLING = 2,
					DIGGING = 3;
	parameter SPLATTERED = 4;

	parameter MAX_FALLS = 20;

	reg[2:0]state;

	always@(posedgeclk)
		if(areset)
			state<=WALK_LEFT;
		else
			case(state)
				WALK_LEFT:begin
					if(bump_left||bump_right)
						state<=WALK_RIGHT;
					else
						state<=WALK_LEFT;
				end
				WALK_RIGHT:begin
					if(bump_left||bump_right)
						state<=WALK_LEFT;
					else
						state<=WALK_RIGHT;
				end
				FALLING:begin
					if(dig)
						state<=DIGGING;
					else
						state<=FALLING;
				end
				DIGGING:begin
					if(!ground)
						state<=FALLING;
					else
						state<=WALK_LEFT;
				end
				SPLATTERED:begin
					state<=SPLATTERED;
				end
			endcase

	always@(posedgeclk)
		if(state==WALK_LEFT)
			walk_left<=1;
		else
			walk_left<=0;

	always@(posedgeclk)
		if(state==WALK_RIGHT)
			walk_right<=1;
		else
			walk_right<=0;

	always@(posedgeclk)
		if(state==FALLING)
			aaah<=1;
		else
			aaah<=0;

	always@(posedgeclk)
		if(state==DIGGING)
			digging<=1;
		else
			digging<=0;

endmodule


