	// States
	parameter WL = 0;
	parameter WR = 1;
	parameter FG = 2;
	parameter DF = 3;
	parameter SF = 4;

	// Internal signals
	reg[1:0]state;

	always@(posedgeclk)
	if(areset)
		state<=WL;
	else
		case(state)
			WL:begin
				if(bump_right)
					state<=WR;
				else
					if(dig)
						state<=DF;
					else
						if(!ground)
							state<=FG;
			end
			WR:begin
				if(bump_left)
					state<=WL;
				else
					if(dig)
						state<=DF;
					else
						if(!ground)
							state<=FG;
			end
			FG:begin
				if(dig)
					state<=DF;
				else
					if(ground)
						state<=WL;
			end
			DF:begin
				if(ground)
					state<=WL;
			end
			SF:begin
				//DoNothing
			end
		endcase

	assignwalk_left=(state==WL);
	assignwalk_right=(state==WR);
	assignaaah=(state==FG);
	assigndigging=(state==DF);

endmodule

	parameter WALK_LEFT = 0, WALK_RIGHT = 1;
	parameter FALLING = 2, DIGGING = 3, SPLATTERED = 4;

	parameter FALLING_TIME = 20;

	reg[1:0]state;

	always@*begin
		case(state)
			WALK_LEFT:begin
				if(bump_left)begin
					walk_left=0;
					walk_right=1;
					aaah=0;
					digging=0;
					state=WALK_RIGHT;
				endelseif(bump_right)begin
					walk_left=1;
					walk_right=0;
					aaah=0;
					digging=0;
					state=WALK_LEFT;
				endelseif(dig)begin
					if(ground)begin
						walk_left=1;
						walk_right=0;
						aaah=0;
						digging=1;
						state=DIGGING;
					endelsebegin
						walk_left=0;
						walk_right=0;
						aaah=0;
						digging=0;
						state=SPLATTERED;
					end
				endelsebegin
					walk_left=1;
					walk_right=0;
					aaah=0;
					digging=0;
					state=WALK_LEFT;
				end
			end
			WALK_RIGHT:begin
				if(bump_right)begin
					walk_left=1;
					walk_right=0;
					aaah=0;
					digging=0;
					state=WALK_LEFT;
				endelseif(bump_left)begin
					walk_left=0;
					walk_right=1;
					aaah=0;
					digging=0;
					state=WALK_RIGHT;
				endelseif(dig)begin
					if(ground)begin
						walk_left=0;
						walk_right=1;
						aaah=0;
						digging=1;
						state=DIGGING;
					endelsebegin
						walk_left=0;
						walk_right=0;
						aaah=0;
						digging=0;
						state=SPLATTERED;
					end
				endelsebegin
					walk_left=0;
					walk_right=1;
					aaah=0;
					digging=0;
					state=WALK_RIGHT;
				end
			end
			FALLING:begin
				if(ground)begin
					walk_left=1;
					walk_right=0;
					aaah=0;
					digging=0;
					state=WALK_LEFT;
				endelsebegin
					walk_left=0;
					walk_right=0;
					aaah=1;
					digging=0;
					state=FALLING;
				end
			end
			DIGGING:begin
				if(ground)begin
					walk_left=1;
					walk_right=0;
					aaah=0;
					digging=0;
					state=WALK_LEFT;
				endelsebegin
					walk_left=0;
					walk_right=0;
					aaah=0;
					digging=1;
					state=DIGGING;
				end
			end
			SPLATTERED:begin
				walk_left=0;
				walk_right=0;
				aaah=0;
				digging=0;
				state=SPLATTERED;
			end
		endcase
	end

	always@(posedgeclk)begin
		if(areset)begin
			state<=WALK_LEFT;
		endelsebegin
			case(state)
				WALK_LEFT:begin
					if(bump_left)begin
						walk_left<=0;
						walk_right<=1;
						aaah<=0;
						digging<=0;
						state<=WALK_RIGHT;
					endelseif(bump_right)begin
						walk_left<=1;
						walk_right<=0;
						aaah<=0;
						digging<=0;
						state<=WALK_LEFT;
					endelseif(dig)begin
						if(ground)begin
							walk_left<=1;
							walk_right<=0;
							aaah<=0;
							digging<=1;
							state<=DIGGING;
						endelsebegin
							walk_left<=0;
							walk_right<=0;
							aaah<=0;
							digging<=0;
							state<=SPLATTERED;
						end
					endelsebegin
						walk_left<=1;
						walk_right<=0;
						aaah<=0;
						digging<=0;
						state<=WALK_LEFT;
					end
				end
				WALK_RIGHT:begin
					if(bump_right)begin
						walk_left<=1;
						walk_right<=0;
						aaah<=0;
						digging<=0;
						state<=WALK_LEFT;
					endelseif(bump_left)begin
						walk_left<=0;
						walk_right<=1;
						aaah<=0;
						digging<=0;
						state<=WALK_RIGHT;
					endelseif(dig)begin
						if(ground)begin
							walk_left<=0;
							walk_right<=1;
							aaah<=0;
							digging<=1;
							state<=DIGGING;
						endelsebegin
							walk_left<=0;
							walk_right<=0;
							aaah<=0;
							digging<=0;
							state<=SPLATTERED;
						end
					endelsebegin
						walk_left<=0;
						walk_right<=1;
						aaah<=0;
						digging<=0;
						state<=WALK_RIGHT;
					end
				end
				FALLING:begin
					if(ground)begin
						walk_left<=1;
						walk_right<=0;
						aaah<=0;
						digging<=0;
						state<=WALK_LEFT;
					endelsebegin
						walk_left<=0;
						walk_right<=0;
						aaah<=1;
						digging<=0;
						state<=FALLING;
					end
				end
				DIGGING:begin
					if(ground)begin
						walk_left<=1;
						walk_right<=0;
						aaah<=0;
						digging<=0;
						state<=WALK_LEFT;
					endelsebegin
						walk_left<=0;
						walk_right<=0;
						aaah<=0;
						digging<=1;
						state<=DIGGING;
					end
				end
				SPLATTERED:begin
					walk_left<=0;
					walk_right<=0;
					aaah<=0;
					digging<=0;
					state<=SPLATTERED;
				end
			endcase
		end
	end

	assigndigging=(dig&&ground);

endmodule


	// State variables
	reg[1:0]state;

	//State transitions
	always@(posedgeclk)begin
		if(areset)
			state<=0;
		else
			case(state)
				0:if(dig==1)
					state<=1;
				else
					state<=2;
				1:state<=2;
				2:if(bump_left==1||bump_right==1)
					state<=3;
				else
					state<=2;
				3:if(dig==1)
					state<=1;
				else
					state<=4;
				4:if(ground==0)
					state<=5;
				else
					state<=2;
				5:if(dig==1)
					state<=1;
				else
					state<=6;
				6:if(ground==0)
					state<=7;
				else
					state<=2;
				7:state<=2;
			endcase
	end

	//Outputs
	assignwalk_left=(state==2||state==3||state==4||state==6);
	assignwalk_right=(state==2||state==3||state==5||state==7);
	assignaaah=(state==5||state==6);
	assigndigging=(state==1||state==3||state==4||state==7);

endmodule

