		if(areset)
			state<=STATE_IDLE;
		else
			case(state)
				STATE_IDLE:begin
					walk_left<=1;
					walk_right<=0;
					aaah<=0;
					digging<=0;
					if(dig)
						state<=STATE_DIGGING;
					else
						if(bump_left||bump_right)
							state<=STATE_FALLING;
						else
							state<=STATE_IDLE;
				end
				STATE_FALLING:begin
					walk_left<=0;
					walk_right<=0;
					aaah<=1;
					digging<=0;
					if(ground)
						state<=STATE_IDLE;
					else
						state<=STATE_FALLING;
				end
				STATE_DIGGING:begin
					walk_left<=0;
					walk_right<=0;
					aaah<=0;
					digging<=1;
					if(!ground)
						state<=STATE_FALLING;
					else
						state<=STATE_DIGGING;
				end
			endcase
	end

	assignwalk_left=(state==STATE_IDLE);
	assignwalk_right=(state==STATE_IDLE);
	assignaaah=(state==STATE_FALLING);
	assigndigging=(state==STATE_DIGGING);

