	//States
	parameter walk_left = 0;
	parameter walk_right = 1;

	//State register
	reg[1:0]state;

	//Next state logic
	always@(*)begin
		case(state)
			walk_left:begin
				if(bump_left||bump_right)begin
					if(bump_left)begin
						state=walk_right;
					endelsebegin
						state=walk_left;
					end
				end
			end
			walk_right:begin
				if(bump_left||bump_right)begin
					if(bump_right)begin
						state=walk_left;
					endelsebegin
						state=walk_right;
					end
				end
			end
		endcase
	end

	//Output logic
	assignwalk_left=(state==walk_left);
	assignwalk_right=(state==walk_right);
	assignaaah=(ground==0);

	//Asynchronousreset
	always@(posedgeclk)begin
		if(areset)begin
			state<=walk_left;
		end
	end

endmodule


