reg[0:0]state;

//Combinational logic block for determining the next state of the state machine
always@(*)begin
	if(state==WL)
		if(bump_left==1'b1)
			next_state=WR;
		else
			next_state=WL;
	else
		if(bump_right==1'b1)
			next_state=WL;
		else
			next_state=WR;
end

//Sequential logic block for updating the state of the state machine
always@(posedgeclk)begin
	if(areset==1'b1)
		state<=WL;
	else
		state<=next_state;
end

//Assigningwalk_leftandwalk_rightbasedonthevalueofthestate
assignwalk_left=(state==WL)?1'b1:1'b0;
assignwalk_right=(state==WR)?1'b1:1'b0;

endmodule

