//Declare a local wire to store the current state
wire[1:0]cur_state;

//Instantiate the state machine and assign the current state to the output
state_machine#(.N(4))state_machine_inst(
    .clk(clk),
    .areset(areset),
    .train_valid(train_valid),
    .train_taken(train_taken),
    .state(cur_state)
);

//Assign the current state to the output
assignstate=cur_state;

endmodule

always@(posedgeclkornegedgeareset)begin
	if(!areset)
		state<=1'b1;
	elseif(train_valid)begin
		if(train_taken&&state<3)
			state<=state+1'b1;
		elseif(!train_taken&&state>0)
			state<=state-1'b1;
		else
			state<=state;
	end
	else
		state<=state;
end

endmodule

//State variables
logic [1:0] state_q;
logic [1:0] state_d;

//Assign the current state to the state_q register
always@(posedgeclk)begin
   state_q<=state_d;
end

//Assign the next state to the state_d register
always@(*)begin
   case(state_q)
        2'b00:begin
            if(train_valid)begin
                if(train_taken)begin
                    state_d=2'b01;
                endelsebegin
                    state_d=2'b00;
                end
            endelsebegin
                state_d=2'b00;
            end
        end
        2'b01:begin
            if(train_valid)begin
                if(train_taken)begin
                    state_d=2'b10;
                endelsebegin
                    state_d=2'b01;
                end
            endelsebegin
                state_d=2'b01;
            end
        end
        2'b10:begin
            if(train_valid)begin
                if(train_taken)begin
                    state_d=2'b11;
                endelsebegin
                    state_d=2'b10;
                end
            endelsebegin
                state_d=2'b10;
            end
        end
        2'b11:begin
            if(train_valid)begin
                if(train_taken)begin
                    state_d=2'b11;
                endelsebegin
                    state_d=2'b10;
                end
            endelsebegin
                state_d=2'b10;
            end
        end
        default:begin
            state_d=2'b00;
        end
    endcase
end

//Asynchronous reset
always@(posedgeclkornegedgeareset)begin
   if(~areset)begin
       state_d<=2'b01;
   endelsebegin
       state_d<=state_d;
