reg[15:0]counter;

initialbegin
	state=3'b000;
end

always@(posedgeclkorposedgerst)begin
	if(rst==1)begin
		state<=3'b000;
		counter<=0;
	endelsebegin
		case(state)
			3'b000:begin//idle
				data_out<=8'h00;
				valid_out<=1'b0;
				if(counter==16'd10000)begin
					state<=3'b010;
					counter<=0;
				endelsebegin
					state<=3'b000;
					counter<=counter+1;
				end
			end
			3'b010:begin//wait
				data_out<=in;
				valid_out<=1'b0;
				if(counter==16'd10000)begin
					state<=3'b011;
					counter<=0;
				endelsebegin
					state<=3'b010;
					counter<=counter+1;
				end
			end
			3'b011:begin//process
				data_out<=in;
				valid_out<=1'b1;
				if(counter==16'd10000)begin
					state<=3'b000;
					counter<=0;
				endelsebegin
					state<=3'b011;
					counter<=counter+1;
				end
			end
			3'b100:begin//done
				data_out<=in;
				valid_out<=1'b0;
				if(counter==16'd10000)begin
					state<=3'b000;
					counter<=0;
				endelsebegin
					state<=3'b100;
					counter<=counter+1;
				end
			end
		endcase
	end
end

