				3'b011:begin
					state<=3'b100;
				end
				3'b100:begin
					state<=3'b000;
				end
				default:begin
					state<=3'b000;
				end
			endcase
			if(state==3'b000)begin
				in_reg<=in;
			endelsebegin
				in_reg<={in_reg,in[7:0]};
			end
			if(state==3'b100)begin
				out_reg<=in_reg;
