always@(posedgeclk)begin
	if(reset)current_state<=2'b00;//resettheStateMachine
	elseif(current_state==2'b00&&w==1'b0)current_state<=2'b01;//transitionfromstateAtoB
	elseif(current_state==2'b00&&w==1'b1)current_state<=2'b00;//transitionfromstateAtoA
	elseif(current_state==2'b01&&w==1'b1)current_state<=2'b10;//transitionfromstateBtoD
	elseif(current_state==2'b01&&w==1'b0)current_state<=2'b00;//transitionfromstateBtoC
	elseif(current_state==2'b10&&w==1'b1)current_state<=2'b00;//transitionfromstateCtoD
	elseif(current_state==2'b10&&w==1'b0)current_state<=2'b11;//transitionfromstateCtoE
	elseif(current_state==2'b11&&w==1'b1)current_state<=2'b00;//transitionfromstateDtoA
	elseif(current_state==2'b11&&w==1'b0)current_state<=2'b10;//transitionfromstateDtoF
	elseif(current_state==2'b00&&w==1'b1)current_state<=2'b10;//transitionfromstateEtoD
