always@(posedgeclkorposedgearset)begin//onrisingedgeofclockorareset
	if(areset==1'b1)begin//ifaresetishigh,resetstatetoB(01)
		state<=2'b01;
	endelsebegin//otherwise,updatestatetothenextstatebasedoninputsignalin
		case(state)//currentstateis00forAand01forB
			2'b00://stateA(00)
				if(in==1'b1)begin//ifinputishigh,stayinstateA(00)
					state<=2'b00;
				endelsebegin//ifinputislow,gotoB(01)
					state<=2'b01;
				end
			2'b01://stateB(01)
				if(in==1'b1)begin//ifinputishigh,stayinstateB(01)
					state<=2'b01;
				endelsebegin//ifinputislow,gotoA(00)
					state<=2'b00;
				end
			default://ifstateisunknown,resetstatetoB(01)
				state<=2'b01;
		endcase
	end
end

assignout=(state==2'b01)?1'b1:1'b0;//outputishighwhenstateisB(01),lowotherwise(00)

endmodule

